size(3inch, 1.5inch);
for ( int y = 0; y <= 5; ++y )
{

draw((0,y)--(18,y));
}
draw((0,0)--(0,5));
draw((6,0)--(6,5));
draw((9,0)--(9,5));
draw((12,0)--(12,5));
draw((15,0)--(15,5));
draw((18,0)--(18,5));

