    GetConsoleScreenBufferInfo(consoleHandle, &consoleInfo);
    short originalColor = consoleInfo.wAttributes;

    // 设置红色前景和蓝色背景
    short color = FOREGROUND_RED | BACKGROUND_BLUE;
    SetConsoleTextAttribute(consoleHandle, color);

    // 输出彩色文本
